// src/App.js
import React, { useState, useEffect } from 'react';
import Header from './components/Header';
import Footer from './components/Footer';

function App() {
  // Ton produit en vedette (ce que tu as déjà)
  const produit = {
    nom: "Iphone 13 256Gb",
    prix: 1299.99,
    description: "Un smartphone performant avec un excellent rapport qualité-prix.",
    image: "https://media.ldlc.com/r1600/ld/products/00/05/88/60/LD0005886075_1.jpg"
  };

  // Nouvel état pour stocker les posts récupérés via l'API
  const [posts, setPosts] = useState([]);

  // Utilisation d'async/await dans useEffect pour appeler l'API
  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const response = await fetch("http://localhost:3001/posts");
        if (!response.ok) {
          throw new Error(`Erreur HTTP : ${response.status}`);
        }
        const data = await response.json();
        console.log("Données récupérées :", data);
        setPosts(data);
      } catch (error) {
        console.error("Erreur lors de l'appel API :", error);
      }
    };

    fetchPosts();
  }, []);

  return (
    <div className="App">
      <Header />
      <main style={{ padding: '2rem', marginBottom: '4rem' }}>
        {/* Section du produit en vedette */}
        <h2>Produit en vedette</h2>
        <div style={{ border: "1px solid #ddd", padding: "1rem", maxWidth: "300px", textAlign: "center" }}>
          <img src={produit.image} alt={produit.nom} style={{ width: "100%" }} />
          <h3>{produit.nom}</h3>
          <p>{produit.description}</p>
          <p><strong>Prix :</strong> {produit.prix}€</p>
        </div>

        {/* Nouvelle section pour afficher les posts récupérés via l'API */}
        <section style={{ marginTop: '2rem' }}>
          <h2>Liste des posts (API)</h2>
          {posts.length === 0 ? (
            <p>Chargement des posts...</p>
          ) : (
            posts.map((post) => (
              <div key={post.id} style={{ border: '1px solid #ddd', padding: '1rem', margin: '1rem 0' }}>
                <h3>{post.title}</h3>
                <p>{post.body}</p>
              </div>
            ))
          )}
        </section>
      </main>
      <Footer />
    </div>
  );
}

export default App;
