// src/App.js
import React from 'react';
import Header from './components/Header';
import Footer from './components/Footer';

function App() {
  const mainStyle = {
    padding: '2rem',
    marginBottom: '4rem' // pour éviter que le contenu ne soit caché par le footer fixe
  };

  return (
    <div className="App">
      <Header />
      <main style={mainStyle}>
        <h2>Bienvenue sur notre boutique en ligne !</h2>
        <p>Ici, vous trouverez une sélection de produits de qualité à des prix attractifs.</p>
        {/* Vous pouvez ajouter d'autres composants ou du contenu ici */}
      </main>
      <Footer />
    </div>
  );
}

export default App;
